angular.module("demo", ["SvgPanZoom"]).controller("SvgPanZoomController", ["$scope", "spz", function ($scope, spz) {
    var spzTrigger = spz(angular.element("#demo-tiger"));
    $scope.ZoomIn = function () {
        spzTrigger.zoomIn();
    };
    $scope.ZoomOut = function () {
        spzTrigger.zoomOut();
    };
    $scope.Reset = function () {
        spzTrigger.resetZoom();
    };
}]);
